select e.*,p.`id` as pid,p.`name` as pname,n.`id` as nid,n.`name` as nname,d.`id` as did,d.`name` as
dname,pos.`id` as posid,pos.`name` as posname from employee e,nation
n,politicsstatus p,department d,position pos where e.`nationId`=n.`id` and e.`politicId`=p.`id` and
e.`departmentId`=d.`id` and e.`posId`=pos.`id`

    and e.name like concat('%',#{emp.name},'%')


    and e.gender =#{emp.gender}


    and e.politicId =#{emp.politicId}


    and e.departmentId =#{emp.departmentId}


    and e.wedlock =#{emp.wedlock}


    and e.tiptopDegree =#{emp.tiptopDegree}


    and e.workLocation like concat('%',#{emp.workLocation},'%')


    and e.workState like concat('%',#{emp.workState},'%')


    and e.resignationReason like concat('%',#{emp.resignationReason},'%')


    and e.workAge = #{emp.workAge}


    and e.beginDate between #{beginDateScope[0]} and #{beginDateScope[1]}


    and e.birthday between date_sub(now(), interval #{ageScope[1]} year) and date_sub(now(), interval #{ageScope[0]} year)


    and e.conversionTime between #{conversionTimeScope[0]} and #{conversionTimeScope[1]}


    and e.notWorkDate between #{notWorkDateScope[0]} and #{notWorkDateScope[1]}


    limit #{page},#{size}

select count(*) from employee e



            and e.name like concat('%',#{emp.name},'%')


