mapper.select:
select p.*, e.name as ename
from per_employee_performance p
left join employee e on p.eid = e.idCard


        and e.name like concat('%',#{empName},'%')


        and p.year = #{year}


        and p.createDate between #{beginDate} and #{endDate}



    limit #{page},#{size}

select count(*) from per_employee_performance p
left join employee e on p.eid = e.idCard


        and e.name like concat('%',#{empName},'%')


        and p.year = #{year}


        and p.createDate between #{beginDate} and #{endDate}

mapper.update:
update per_employee_performance
eid = #{eid}, year = #{year}, month1 = #{month1}, month2 = #{month2}, month3 = #{month3}, month4 = #{month4}, month5 = #{month5}, month6 = #{month6}, month7 = #{month7}, month8 = #{month8}, month9 = #{month9}, month10 = #{month10}, month11 = #{month11}, month12 = #{month12}, updateDate = #{updateDate},
where id = #{id}

update per_employee_performance
set eid = #{eid}, year = #{year}, month1 = #{month1}, month2 = #{month2}, month3 = #{month3}, month4 = #{month4}, month5 = #{month5}, month6 = #{month6}, month7 = #{month7}, month8 = #{month8}, month9 = #{month9}, month10 = #{month10}, month11 = #{month11}, month12 = #{month12}, updateDate = #{updateDate}
where id = #{id}
