mapper.select:
select p.*, e.name as ename
from per_executive_performance p
left join employee e on p.eid = e.idCard


        and e.name like concat('%',#{empName},'%')


        and p.year = #{year}


        and p.createDate between #{beginDate} and #{endDate}



    limit #{page},#{size}

select count(*) from per_executive_performance p
left join employee e on p.eid = e.idCard


        and e.name like concat('%',#{empName},'%')


        and p.year = #{year}


        and p.createDate between #{beginDate} and #{endDate}

mapper.update:
update per_executive_performance
eid = #{eid}, year = #{year}, quarter1 = #{quarter1}, quarter2 = #{quarter2}, quarter3 = #{quarter3}, quarter4 = #{quarter4}, updateDate = #{updateDate},
where id = #{id}

update per_executive_performance
set eid = #{eid}, year = #{year}, quarter1 = #{quarter1}, quarter2 = #{quarter2}, quarter3 = #{quarter3}, quarter4 = #{quarter4}, updateDate = #{updateDate}
where id = #{id}
